package com.masai;

public class cricket_score {

	static void score(int a, int b, int c, int d, int e)
	{
		int one = 1*a;
		int two = 2*b;
		int three = 3*c;
		int four = 4*d;
		int six = 6*e;
		
		System.out.println(one+two+three+four+six);
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		score(1,1,1,1,1);
	}

}
